dict.string:
#import "FacebookServiceObjectManager.h"
#import <FacebookSDK/FacebookSDK.h>

static NSString * const kFacebookAppID = @"<# Facebook App ID #>";
static NSString * const kFacebookBaseURL = @"https://graph.facebook.com";

@implementation FacebookServiceObjectManager

static FacebookHelper *sharedInstance;
+ (instacetype)sharedManager{
    static dispatch_once_t onceToken;
    dispatch_once(&onceToken, ^{
        sharedInstance = [[FacebookHelper alloc] initWithHTTPClient:[AFHTTPClient clientWithBaseURL: [NSURL URLWithString:kFacebookBaseURL]]];
    });

#pragma mark - Helpers
- (NSDictionary *)completeRequestParametersForParameters:(NSDictionary *)params
{
	NSMutableDictionary *fullParameters = [NSMutableDictionary new];
    if(params){
        [fullParameters setValuesForKeysWithDictionary:params];
    }
    if([FBSession activeSession].accessTokenData.accessToken){
        fullParameters[@"access_token"] = [FBSession activeSession].accessTokenData.accessToken;
    }

- (void)openSessionIfTokenLoaded
{
    if([FBSession activeSession].state == FBSessionStateCreatedTokenLoaded){

        if(![[FBSession activeSession].permissions containsObject:[[CQFacebookAuthInfo FBWritePermissions] firstObject]]){
            [FBSession openActiveSessionWithReadPermissions:[CQFacebookAuthInfo FBReadPermissions]
                                               allowLoginUI:YES
                                          completionHandler:nil];
        }
        else if([[FBSession activeSession].permissions containsObject: [[CQFacebookAuthInfo FBWritePermissions] firstObject]]){
            [FBSession openActiveSessionWithPublishPermissions:[CQFacebookAuthInfo FBWritePermissions]
                                               defaultAudience:FBSessionDefaultAudienceEveryone
                                                  allowLoginUI:YES
                                             completionHandler:nil];
        }
    }
